<template>
  <div class="page">
    <CommonHeader :title="t('mallStore')">
      <div class="hml-r integral"><i class="iconfont icon-shandian" />{{ userStore.userInfo.integral ? moneyFormat(userStore.userInfo.integral) : '0.00' }}</div>
    </CommonHeader>
    <main class="main">
      <div class="marketplace-box">
        <!-- vip等级 -->
        <div v-if="vipList.length > 0" class="step-vip">
          <div v-for="(item, index) of vipList" :key="index" :class="userStore.userInfo.vipCode == item.code ? 'sv-item active' : 'sv-item'">
            <label>x{{ item.integral }}</label>
            <span />
            <img :src="getAssetsFile(`grade/${item.code}.png`)" />
          </div>
        </div>
        <!-- tab切换 -->
        <div class="line-tabs" id="mk-tabs">
          <span :class="{ active: tab == 'rewards' }" @click="toggleTab('rewards')">{{ t('rewardsBig') }}</span>
          <span :class="{ active: tab == 'stats' }" @click="toggleTab('stats')">{{ t('myStats') }}</span>
        </div>
        <div class="tab-content">
          <div v-show="tab == 'rewards'" class="tc-box">
            <!-- 赚取积分 -->
            <div class="earn-points">
              <dl>
                <dt>{{ t('earnPoints') }}</dt>
                <dd>
                  <a @click="router.push({ name: 'sports' })">
                    <div class="a-l">
                      <i class="iconfont icon-ball-1" />
                    </div>
                    <div class="a-m">
                      <h3>{{ t('sports') }}</h3>
                      <span>{{ t('betAndEarn') }}</span>
                    </div>
                    <div class="a-r">
                      <i class="iconfont icon-enter" />
                    </div>
                  </a>
                </dd>
                <dd>
                  <a @click="router.push({ name: 'casino' })">
                    <div class="a-l">
                      <i class="iconfont icon-qipai3" />
                    </div>
                    <div class="a-m">
                      <h3>{{ t('liveCasino') }}</h3>
                      <span>{{ t('placeAndEarn') }}</span>
                    </div>
                    <div class="a-r">
                      <i class="iconfont icon-enter" />
                    </div>
                  </a>
                </dd>
                <dd>
                  <a @click="router.push({ name: 'slots' })">
                    <div class="a-l">
                      <i class="iconfont icon-losts" />
                    </div>
                    <div class="a-m">
                      <h3>{{ t('slots') }}</h3>
                      <span>{{ t('spinAndEarn') }}</span>
                    </div>
                    <div class="a-r">
                      <i class="iconfont icon-enter" />
                    </div>
                  </a>
                </dd>
              </dl>
            </div>
            <!-- 商品列表 -->
            <div class="exclusive-rewards">
              <h2>{{ t('exclusiveRewards') }}</h2>
              <div v-if="goodsList.length > 0" class="list">
                <template v-for="(item, index) of goodsList">
                  <div v-if="item.productType != 1" :key="index" class="item" @click="showDetails(item)">
                    <div class="er-img" v-lazy:background-image="appStore.cdnurl + item.images.split(',')[0]" />
                    <div class="er-title">{{ item.name }}</div>
                    <div class="er-mark">{{ item.intro }}</div>
                    <div v-if="![3, 4].includes(item.productType)" class="er-price">
                      <i class="iconfont icon-shandian" />
                      <b>{{ moneyFormat(item.price) }}</b>
                    </div>
                    <div v-else class="er-exhange">
                      <a class="btn btn-primary">{{ t('buyNow') }}</a>
                    </div>
                  </div>
                </template>
              </div>
            </div>
            <!-- 筹码 -->
            <div class="quick-buys">
              <h2>{{ t('quickBuys') }}</h2>
              <div v-if="goodsList.length > 0" class="list">
                <template v-for="(item, index) of goodsList" :key="index">
                  <div v-if="item.productType == 1" class="item" @click="showDetails(item)">
                    <div class="i-card">
                      <div class="ic-t sbg1">
                        <div class="ic-info">
                          <b><i class="iconfont icon-tether" />{{ item.amount }}</b>
                          <span>{{ item.intro }} x {{ t('multiplier') }}</span>
                        </div>
                        <div class="ic-bg" />
