<script setup lang="ts">
import { ref, reactive } from 'vue'
import { useRouter } from 'vue-router'
import CommonHeader from '@/components/layout/CommonHeader.vue'

import { getGoodsListApi, exhangeGoodsApi, getIntegralRecordApi, getIntegralVipApi } from '@/api/mall/index'
import { getGoodsListItem, getIntegralRecordItem, getIntegralRecordData, getIntegralVipItem, exhangeGoodsData } from '@/api/mall/types'
import { useUserStore } from '@/store/modules/user'
import { useAppStore } from '@/store/modules/app'
import { getAssetsFile, moneyFormat } from '@/utils'
import { useI18n } from 'vue-i18n'

// import { cloneDeep } from 'lodash-es'
import { showToast, ConfigProvider, Popup, PullRefresh, List, showConfirmDialog, Form, CellGroup, Field, RadioGroup, Radio, Picker, Button } from 'vant'
import { Swiper, SwiperSlide } from 'swiper/vue'
import { FreeMode, Navigation, Thumbs } from 'swiper/modules'
import 'swiper/css'
import 'swiper/css/free-mode'
import 'swiper/css/navigation'
import 'swiper/css/thumbs'

const router = useRouter()
const { t } = useI18n()
const userStore = useUserStore()
const appStore = useAppStore()

// tab切换
let tab = ref('rewards')

const exchangeTicket = ref('exchangeTicket')
const exchangeHotel = ref('exchangeHotel')

// 商品詳情
const currentGoodsItem = reactive<getGoodsListItem>({
  id: '',
  price: '',
  images: '',
  amount: '',
  productType: 0,
  currencyCode: '',
  name: '',
  intro: '',
  description: ''
})

const docType = [
  { text: t('idCard'), value: t('idCard') },
  { text: t('passport'), value: t('passport') }
]

// 机票订单
const showDocTypePicker = ref(false)
const exhangeGoodsParams = reactive({
  Id: '',
  AirTicketOrder: {
    Name: '',
    Sex: t('male'),
    DocumentType: t('idCard'),
    DocumentNumber: '',
    AirlineCompany: '',
    PlaceOfDeparture: '',
    Destination: '',
    OnewayOrRoundtrip: t('oneway'),
    EngineroomType: t('economyClass'),
    Otherservices: ''
  },
  GroggeryOrder: {
    Name: '',
    Sex: t('male'),
    HotelName: '',
    HotelCountry: '',
    TheCityRegion: '',
    RoomType: t('presidentialSuite'),
    NumberOfRooms: '',
    NumberOfDaysRequired: '',
    OtherServices: ''
  }
})

// swiper
const modules = [FreeMode, Navigation, Thumbs]
let showGoodsDetails = ref(false)
let thumbsSwiper = ref(null)
const setThumbsSwiper = (swiper: any) => {
  thumbsSwiper.value = swiper
}

// VIP等级
let vipList = ref<getIntegralVipItem[]>([])

// 商品列表参数
let goodsList = ref<getGoodsListItem[]>([])

// 获取积分记录
let listLoading = ref(false)
let refreshing = ref(false)
let finished = ref(false)
let error = ref(false)
const nodata = ref(false)
const query = reactive<getIntegralRecordData>({
  AdjustType: 1,
  PageIndex: 1,
  PageSize: 10,
  noLoading: false
})
let integralList = ref<getIntegralRecordItem[]>([])

// 顶部商城和积分记录切换
const toggleTab = (tabs: any) => {
  tab.value = tabs
  if (tab.value == 'stats') {
    toggleRecord(1)
  }
}

// 获取积分商城顶部vip等级
const getIntegralVip = () => {
  getIntegralVipApi()
    .then((resp) => {
      vipList.value = resp.data
    })
    .catch((error) => {
      console.log(error)
    })
}

// 获取商品列表
const getGoodsList = () => {
  getGoodsListApi()
    .then((resp) => {
      goodsList.value = resp.data
    })
    .catch((error) => {
      console.log(error)
    })
}

// 积分兑换商品
const exhangeGoods = () => {
  console.log(exchangeTicket.value)
  if (!userStore.userInfo.id) {
    showConfirmDialog({
      title: t('tips.noLogin'),
      message: t('tips.goLogin')
    })
      .then(() => {
        router.push({ name: 'login' })
      })
      .catch(() => {
        return false
      })
  } else {
    let data = <exhangeGoodsData>{}
    if (currentGoodsItem.productType == 0 || currentGoodsItem.productType == 1) {
      data = { Id: currentGoodsItem.id }
    } else if (currentGoodsItem.productType == 3) {
      data = { Id: currentGoodsItem.id, AirTicketOrder: exhangeGoodsParams.AirTicketOrder }
    } else if (currentGoodsItem.productType == 4) {
      data = { Id: currentGoodsItem.id, GroggeryOrder: exhangeGoodsParams.GroggeryOrder }
    }

    exhangeGoodsApi(data)
      .then(() => {
        showToast(t('tips.exhangeSuccess'))
        showGoodsDetails.value = false
        userStore.getUserInfo({ noLoading: true })
      })
      .catch((error) => {
        console.log(error)
      })
  }
}

// 切换积分记录
const toggleRecord = (adjustType: number) => {
  query.AdjustType = adjustType
  query.PageIndex = 1
  integralList.value = []
  getIntegralRecord()
}

// 获取积分记录
const getIntegralRecord = () => {
  // 未登录则不显示数据
  if (!userStore.userInfo.id) {
    nodata.value = true
    return false
  }
  getIntegralRecordApi(query)
    .then((resp) => {
      if (refreshing.value) {
        integralList.value = resp.data.items
      } else {
        integralList.value.push(...resp.data.items)
      }
      nodata.value = integralList.value.length == 0
      refreshing.value = false
      finished.value = resp.data.items.length < parseInt(query.PageSize.toString())
      listLoading.value = false
    })
    .catch((error) => {
      listLoading.value = false
      console.log(error)
    })
}

// 刷新
const fresh = () => {
  query.noLoading = true
  query.PageIndex = 1
  getIntegralRecord()
}

// 上拉加载更多数据
const loadData = () => {
  query.noLoading = true
  query.PageIndex = parseInt(query.PageIndex.toString()) + 1
  getIntegralRecord()
}

const showDetails = (item: getGoodsListItem) => {
  Object.assign(currentGoodsItem, item)
  showGoodsDetails.value = true
}

const confirmIdCard = (selected: any) => {
  exhangeGoodsParams.AirTicketOrder.DocumentType = selected.selectedValues[0]
  showDocTypePicker.value = false
}

getIntegralVip()
getGoodsList()
getIntegralRecord()
</script>
